"use strict";(globalThis.webpackChunkphysical_ai_book_humanoid=globalThis.webpackChunkphysical_ai_book_humanoid||[]).push([[15],{8453(n,i,e){e.d(i,{R:()=>a,x:()=>l});var r=e(6540);const o={},t=r.createContext(o);function a(n){const i=r.useContext(t);return r.useMemo(function(){return"function"==typeof n?n(i):{...i,...n}},[i,n])}function l(n){let i;return i=n.disableParentContext?"function"==typeof n.components?n.components(o):n.components||o:a(n.components),r.createElement(t.Provider,{value:i},n.children)}},9038(n,i,e){e.r(i),e.d(i,{assets:()=>s,contentTitle:()=>l,default:()=>m,frontMatter:()=>a,metadata:()=>r,toc:()=>x});const r=JSON.parse('{"id":"examples/urdf-example","title":"URDF Example","description":"Complete Humanoid Robot URDF Model with WHAT/WHY Comments","source":"@site/docs/examples/urdf-example.md","sourceDirName":"examples","slug":"/examples/urdf-example","permalink":"/docs/examples/urdf-example","draft":false,"unlisted":false,"editUrl":"https://github.com/Panaversity/physical_ai_book_humanoid/tree/main/docs/examples/urdf-example.md","tags":[],"version":"current","frontMatter":{}}');var o=e(4848),t=e(8453);const a={},l="URDF Example",s={},x=[{value:"Complete Humanoid Robot URDF Model with WHAT/WHY Comments",id:"complete-humanoid-robot-urdf-model-with-whatwhy-comments",level:2},{value:"Xacro Example",id:"xacro-example",level:2},{value:"Dependencies",id:"dependencies",level:2},{value:"How to Use",id:"how-to-use",level:2},{value:"Key Components Explained",id:"key-components-explained",level:2}];function y(n){const i={code:"code",h1:"h1",h2:"h2",header:"header",li:"li",ol:"ol",p:"p",pre:"pre",strong:"strong",ul:"ul",...(0,t.R)(),...n.components};return(0,o.jsxs)(o.Fragment,{children:[(0,o.jsx)(i.header,{children:(0,o.jsx)(i.h1,{id:"urdf-example",children:"URDF Example"})}),"\n",(0,o.jsx)(i.h2,{id:"complete-humanoid-robot-urdf-model-with-whatwhy-comments",children:"Complete Humanoid Robot URDF Model with WHAT/WHY Comments"}),"\n",(0,o.jsx)(i.pre,{children:(0,o.jsx)(i.code,{className:"language-xml",children:'<?xml version="1.0"?>\n\x3c!-- Example: Complete Humanoid Robot URDF --\x3e\n\x3c!-- WHAT: This URDF defines a complete humanoid robot with legs, arms, and head --\x3e\n\x3c!-- WHY: To demonstrate a realistic humanoid model with proper kinematic chains --\x3e\n\n<robot name="tutorial_humanoid" xmlns:xacro="http://www.ros.org/wiki/xacro">\n\n  \x3c!-- Gazebo plugin configuration --\x3e\n  \x3c!-- WHAT: This configures the gazebo_ros_control plugin for simulation --\x3e\n  \x3c!-- WHY: The plugin enables ROS 2 to communicate with Gazebo for physics simulation --\x3e\n  <gazebo>\n    <plugin name="gazebo_ros_control" filename="libgazebo_ros_control.so">\n      <robotNamespace>/humanoid</robotNamespace>\n    </plugin>\n  </gazebo>\n\n  \x3c!-- Base link (pelvis) - the main body of the robot --\x3e\n  \x3c!-- WHAT: This defines the base link of the robot, which serves as the root of the kinematic tree --\x3e\n  \x3c!-- WHY: All other links are connected relative to this base link --\x3e\n  <link name="base_link">\n    <inertial>\n      \x3c!-- Mass and inertia properties for physics simulation --\x3e\n      \x3c!-- WHAT: These properties define the physical characteristics of the link for physics simulation --\x3e\n      \x3c!-- WHY: Accurate mass and inertia properties are essential for realistic physics simulation --\x3e\n      <mass value="15.0"/>\n      <origin xyz="0 0 0.1" rpy="0 0 0"/>\n      <inertia ixx="0.5" ixy="0.0" ixz="0.0" iyy="0.5" iyz="0.0" izz="0.5"/>\n    </inertial>\n    <visual>\n      \x3c!-- Visual properties for rendering in simulation/visualization tools --\x3e\n      \x3c!-- WHAT: These properties define how the link appears visually --\x3e\n      \x3c!-- WHY: Visual representation is important for simulation visualization and debugging --\x3e\n      <origin xyz="0 0 0.1" rpy="0 0 0"/>\n      <geometry>\n        <box size="0.2 0.25 0.2"/>\n      </geometry>\n      <material name="gray">\n        <color rgba="0.5 0.5 0.5 1"/>\n      </material>\n    </visual>\n    <collision>\n      \x3c!-- Collision properties for physics simulation --\x3e\n      \x3c!-- WHAT: These properties define the collision boundaries for physics simulation --\x3e\n      \x3c!-- WHY: Collision properties are needed for realistic interaction with the environment --\x3e\n      <origin xyz="0 0 0.1" rpy="0 0 0"/>\n      <geometry>\n        <box size="0.2 0.25 0.2"/>\n      </geometry>\n    </collision>\n  </link>\n\n  \x3c!-- Torso link --\x3e\n  \x3c!-- WHAT: This defines the torso link connected to the base --\x3e\n  \x3c!-- WHY: The torso connects the legs to the arms and head --\x3e\n  <link name="torso">\n    <inertial>\n      <mass value="10.0"/>\n      <origin xyz="0 0 0.3" rpy="0 0 0"/>\n      <inertia ixx="0.8" ixy="0.0" ixz="0.0" iyy="0.8" iyz="0.0" izz="0.8"/>\n    </inertial>\n    <visual>\n      <origin xyz="0 0 0.3" rpy="0 0 0"/>\n      <geometry>\n        <box size="0.2 0.2 0.6"/>\n      </geometry>\n      <material name="red">\n        <color rgba="0.8 0.2 0.2 1"/>\n      </material>\n    </visual>\n    <collision>\n      <origin xyz="0 0 0.3" rpy="0 0 0"/>\n      <geometry>\n        <box size="0.2 0.2 0.6"/>\n      </geometry>\n    </collision>\n  </link>\n\n  \x3c!-- Joint connecting base to torso --\x3e\n  \x3c!-- WHAT: This defines a fixed joint connecting the base to the torso --\x3e\n  \x3c!-- WHY: Fixed joints are used when two links should not move relative to each other --\x3e\n  <joint name="torso_joint" type="fixed">\n    <parent link="base_link"/>\n    <child link="torso"/>\n    <origin xyz="0 0 0.2" rpy="0 0 0"/>\n  </joint>\n\n  \x3c!-- Head link --\x3e\n  \x3c!-- WHAT: This defines the head link for the robot --\x3e\n  \x3c!-- WHY: The head contains sensors and allows for gaze direction --\x3e\n  <link name="head">\n    <inertial>\n      <mass value="3.0"/>\n      <origin xyz="0 0 0.1" rpy="0 0 0"/>\n      <inertia ixx="0.05" ixy="0.0" ixz="0.0" iyy="0.05" iyz="0.0" izz="0.05"/>\n    </inertial>\n    <visual>\n      <origin xyz="0 0 0.1" rpy="0 0 0"/>\n      <geometry>\n        <sphere radius="0.12"/>\n      </geometry>\n      <material name="white">\n        <color rgba="0.9 0.9 0.9 1"/>\n      </material>\n    </visual>\n    <collision>\n      <origin xyz="0 0 0.1" rpy="0 0 0"/>\n      <geometry>\n        <sphere radius="0.12"/>\n      </geometry>\n    </collision>\n  </link>\n\n  \x3c!-- Joint connecting torso to head --\x3e\n  \x3c!-- WHAT: This defines a revolute joint for head movement --\x3e\n  \x3c!-- WHY: Revolute joints allow rotation around a single axis, useful for head movement --\x3e\n  <joint name="neck_joint" type="revolute">\n    <parent link="torso"/>\n    <child link="head"/>\n    <origin xyz="0 0 0.6" rpy="0 0 0"/>\n    <axis xyz="0 1 0"/>  \x3c!-- Y-axis rotation for head nodding --\x3e\n    <limit lower="-0.5" upper="0.5" effort="10" velocity="2.0"/>\n  </joint>\n\n  \x3c!-- Left Arm Chain --\x3e\n  \x3c!-- WHAT: This defines the complete kinematic chain for the left arm --\x3e\n  \x3c!-- WHY: The arm chain allows for manipulation tasks --\x3e\n  <link name="left_shoulder">\n    <inertial>\n      <mass value="1.0"/>\n      <origin xyz="0 -0.05 0" rpy="0 0 0"/>\n      <inertia ixx="0.01" ixy="0.0" ixz="0.0" iyy="0.01" iyz="0.0" izz="0.01"/>\n    </inertial>\n    <visual>\n      <origin xyz="0 -0.05 0" rpy="0 0 0"/>\n      <geometry>\n        <cylinder radius="0.05" length="0.1"/>\n      </geometry>\n      <material name="blue">\n        <color rgba="0.2 0.2 0.8 1"/>\n      </material>\n    </visual>\n    <collision>\n      <origin xyz="0 -0.05 0" rpy="0 0 0"/>\n      <geometry>\n        <cylinder radius="0.05" length="0.1"/>\n      </geometry>\n    </collision>\n  </link>\n\n  <joint name="left_shoulder_yaw" type="revolute">\n    <parent link="torso"/>\n    <child link="left_shoulder"/>\n    <origin xyz="0.05 0.1 0.3" rpy="0 0 0"/>\n    <axis xyz="0 0 1"/>  \x3c!-- Z-axis rotation for shoulder yaw --\x3e\n    <limit lower="-1.57" upper="1.57" effort="50" velocity="2.0"/>\n  </joint>\n\n  <link name="left_upper_arm">\n    <inertial>\n      <mass value="1.5"/>\n      <origin xyz="0 0 -0.15" rpy="0 0 0"/>\n      <inertia ixx="0.03" ixy="0.0" ixz="0.0" iyy="0.03" iyz="0.0" izz="0.03"/>\n    </inertial>\n    <visual>\n      <origin xyz="0 0 -0.15" rpy="0 0 0"/>\n      <geometry>\n        <cylinder radius="0.04" length="0.3"/>\n      </geometry>\n      <material name="blue">\n        <color rgba="0.2 0.2 0.8 1"/>\n      </material>\n    </visual>\n    <collision>\n      <origin xyz="0 0 -0.15" rpy="0 0 0"/>\n      <geometry>\n        <cylinder radius="0.04" length="0.3"/>\n      </geometry>\n    </collision>\n  </link>\n\n  <joint name="left_shoulder_pitch" type="revolute">\n    <parent link="left_shoulder"/>\n    <child link="left_upper_arm"/>\n    <origin xyz="0 0 0" rpy="0 0 0"/>\n    <axis xyz="1 0 0"/>  \x3c!-- X-axis rotation for shoulder pitch --\x3e\n    <limit lower="-2.0" upper="1.0" effort="50" velocity="2.0"/>\n  </joint>\n\n  <link name="left_lower_arm">\n    <inertial>\n      <mass value="1.0"/>\n      <origin xyz="0 0 -0.12" rpy="0 0 0"/>\n      <inertia ixx="0.02" ixy="0.0" ixz="0.0" iyy="0.02" iyz="0.0" izz="0.02"/>\n    </inertial>\n    <visual>\n      <origin xyz="0 0 -0.12" rpy="0 0 0"/>\n      <geometry>\n        <cylinder radius="0.035" length="0.24"/>\n      </geometry>\n      <material name="blue">\n        <color rgba="0.2 0.2 0.8 1"/>\n      </material>\n    </visual>\n    <collision>\n      <origin xyz="0 0 -0.12" rpy="0 0 0"/>\n      <geometry>\n        <cylinder radius="0.035" length="0.24"/>\n      </geometry>\n    </collision>\n  </link>\n\n  <joint name="left_elbow" type="revolute">\n    <parent link="left_upper_arm"/>\n    <child link="left_lower_arm"/>\n    <origin xyz="0 0 -0.3" rpy="0 0 0"/>\n    <axis xyz="1 0 0"/>  \x3c!-- X-axis rotation for elbow --\x3e\n    <limit lower="-2.5" upper="0.0" effort="30" velocity="2.0"/>\n  </joint>\n\n  <link name="left_hand">\n    <inertial>\n      <mass value="0.5"/>\n      <origin xyz="0 0 -0.05" rpy="0 0 0"/>\n      <inertia ixx="0.005" ixy="0.0" ixz="0.0" iyy="0.005" iyz="0.0" izz="0.005"/>\n    </inertial>\n    <visual>\n      <origin xyz="0 0 -0.05" rpy="0 0 0"/>\n      <geometry>\n        <box size="0.1 0.08 0.1"/>\n      </geometry>\n      <material name="blue">\n        <color rgba="0.2 0.2 0.8 1"/>\n      </material>\n    </visual>\n    <collision>\n      <origin xyz="0 0 -0.05" rpy="0 0 0"/>\n      <geometry>\n        <box size="0.1 0.08 0.1"/>\n      </geometry>\n    </collision>\n  </link>\n\n  <joint name="left_wrist" type="revolute">\n    <parent link="left_lower_arm"/>\n    <child link="left_hand"/>\n    <origin xyz="0 0 -0.24" rpy="0 0 0"/>\n    <axis xyz="0 1 0"/>  \x3c!-- Y-axis rotation for wrist --\x3e\n    <limit lower="-1.0" upper="1.0" effort="10" velocity="2.0"/>\n  </joint>\n\n  \x3c!-- Right Arm Chain (mirror of left) --\x3e\n  \x3c!-- WHAT: This defines the complete kinematic chain for the right arm --\x3e\n  \x3c!-- WHY: Symmetry is important for humanoid robots to perform balanced tasks --\x3e\n  <link name="right_shoulder">\n    <inertial>\n      <mass value="1.0"/>\n      <origin xyz="0 0.05 0" rpy="0 0 0"/>\n      <inertia ixx="0.01" ixy="0.0" ixz="0.0" iyy="0.01" iyz="0.0" izz="0.01"/>\n    </inertial>\n    <visual>\n      <origin xyz="0 0.05 0" rpy="0 0 0"/>\n      <geometry>\n        <cylinder radius="0.05" length="0.1"/>\n      </geometry>\n      <material name="blue">\n        <color rgba="0.2 0.2 0.8 1"/>\n      </material>\n    </visual>\n    <collision>\n      <origin xyz="0 0.05 0" rpy="0 0 0"/>\n      <geometry>\n        <cylinder radius="0.05" length="0.1"/>\n      </geometry>\n    </collision>\n  </link>\n\n  <joint name="right_shoulder_yaw" type="revolute">\n    <parent link="torso"/>\n    <child link="right_shoulder"/>\n    <origin xyz="0.05 -0.1 0.3" rpy="0 0 0"/>\n    <axis xyz="0 0 1"/>  \x3c!-- Z-axis rotation for shoulder yaw --\x3e\n    <limit lower="-1.57" upper="1.57" effort="50" velocity="2.0"/>\n  </joint>\n\n  <link name="right_upper_arm">\n    <inertial>\n      <mass value="1.5"/>\n      <origin xyz="0 0 -0.15" rpy="0 0 0"/>\n      <inertia ixx="0.03" ixy="0.0" ixz="0.0" iyy="0.03" iyz="0.0" izz="0.03"/>\n    </inertial>\n    <visual>\n      <origin xyz="0 0 -0.15" rpy="0 0 0"/>\n      <geometry>\n        <cylinder radius="0.04" length="0.3"/>\n      </geometry>\n      <material name="blue">\n        <color rgba="0.2 0.2 0.8 1"/>\n      </material>\n    </visual>\n    <collision>\n      <origin xyz="0 0 -0.15" rpy="0 0 0"/>\n      <geometry>\n        <cylinder radius="0.04" length="0.3"/>\n      </geometry>\n    </collision>\n  </link>\n\n  <joint name="right_shoulder_pitch" type="revolute">\n    <parent link="right_shoulder"/>\n    <child link="right_upper_arm"/>\n    <origin xyz="0 0 0" rpy="0 0 0"/>\n    <axis xyz="1 0 0"/>  \x3c!-- X-axis rotation for shoulder pitch --\x3e\n    <limit lower="-1.0" upper="2.0" effort="50" velocity="2.0"/>\n  </joint>\n\n  <link name="right_lower_arm">\n    <inertial>\n      <mass value="1.0"/>\n      <origin xyz="0 0 -0.12" rpy="0 0 0"/>\n      <inertia ixx="0.02" ixy="0.0" ixz="0.0" iyy="0.02" iyz="0.0" izz="0.02"/>\n    </inertial>\n    <visual>\n      <origin xyz="0 0 -0.12" rpy="0 0 0"/>\n      <geometry>\n        <cylinder radius="0.035" length="0.24"/>\n      </geometry>\n      <material name="blue">\n        <color rgba="0.2 0.2 0.8 1"/>\n      </material>\n    </visual>\n    <collision>\n      <origin xyz="0 0 -0.12" rpy="0 0 0"/>\n      <geometry>\n        <cylinder radius="0.035" length="0.24"/>\n      </geometry>\n    </collision>\n  </link>\n\n  <joint name="right_elbow" type="revolute">\n    <parent link="right_upper_arm"/>\n    <child link="right_lower_arm"/>\n    <origin xyz="0 0 -0.3" rpy="0 0 0"/>\n    <axis xyz="1 0 0"/>  \x3c!-- X-axis rotation for elbow --\x3e\n    <limit lower="0.0" upper="2.5" effort="30" velocity="2.0"/>\n  </joint>\n\n  <link name="right_hand">\n    <inertial>\n      <mass value="0.5"/>\n      <origin xyz="0 0 -0.05" rpy="0 0 0"/>\n      <inertia ixx="0.005" ixy="0.0" ixz="0.0" iyy="0.005" iyz="0.0" izz="0.005"/>\n    </inertial>\n    <visual>\n      <origin xyz="0 0 -0.05" rpy="0 0 0"/>\n      <geometry>\n        <box size="0.1 0.08 0.1"/>\n      </geometry>\n      <material name="blue">\n        <color rgba="0.2 0.2 0.8 1"/>\n      </material>\n    </visual>\n    <collision>\n      <origin xyz="0 0 -0.05" rpy="0 0 0"/>\n      <geometry>\n        <box size="0.1 0.08 0.1"/>\n      </geometry>\n    </collision>\n  </link>\n\n  <joint name="right_wrist" type="revolute">\n    <parent link="right_lower_arm"/>\n    <child link="right_hand"/>\n    <origin xyz="0 0 -0.24" rpy="0 0 0"/>\n    <axis xyz="0 1 0"/>  \x3c!-- Y-axis rotation for wrist --\x3e\n    <limit lower="-1.0" upper="1.0" effort="10" velocity="2.0"/>\n  </joint>\n\n  \x3c!-- Left Leg Chain --\x3e\n  \x3c!-- WHAT: This defines the complete kinematic chain for the left leg --\x3e\n  \x3c!-- WHY: The leg chain enables locomotion and balance for the humanoid robot --\x3e\n  <link name="left_hip">\n    <inertial>\n      <mass value="2.0"/>\n      <origin xyz="0 -0.05 0" rpy="0 0 0"/>\n      <inertia ixx="0.02" ixy="0.0" ixz="0.0" iyy="0.02" iyz="0.0" izz="0.02"/>\n    </inertial>\n    <visual>\n      <origin xyz="0 -0.05 0" rpy="0 0 0"/>\n      <geometry>\n        <cylinder radius="0.06" length="0.1"/>\n      </geometry>\n      <material name="green">\n        <color rgba="0.2 0.8 0.2 1"/>\n      </material>\n    </visual>\n    <collision>\n      <origin xyz="0 -0.05 0" rpy="0 0 0"/>\n      <geometry>\n        <cylinder radius="0.06" length="0.1"/>\n      </geometry>\n    </collision>\n  </link>\n\n  <joint name="left_hip_yaw" type="revolute">\n    <parent link="base_link"/>\n    <child link="left_hip"/>\n    <origin xyz="0 0.08 0" rpy="0 0 0"/>\n    <axis xyz="0 0 1"/>  \x3c!-- Z-axis rotation for hip yaw --\x3e\n    <limit lower="-0.5" upper="0.5" effort="100" velocity="1.0"/>\n  </joint>\n\n  <link name="left_thigh">\n    <inertial>\n      <mass value="3.0"/>\n      <origin xyz="0 0 -0.2" rpy="0 0 0"/>\n      <inertia ixx="0.1" ixy="0.0" ixz="0.0" iyy="0.1" iyz="0.0" izz="0.1"/>\n    </inertial>\n    <visual>\n      <origin xyz="0 0 -0.2" rpy="0 0 0"/>\n      <geometry>\n        <cylinder radius="0.06" length="0.4"/>\n      </geometry>\n      <material name="green">\n        <color rgba="0.2 0.8 0.2 1"/>\n      </material>\n    </visual>\n    <collision>\n      <origin xyz="0 0 -0.2" rpy="0 0 0"/>\n      <geometry>\n        <cylinder radius="0.06" length="0.4"/>\n      </geometry>\n    </collision>\n  </link>\n\n  <joint name="left_hip_pitch" type="revolute">\n    <parent link="left_hip"/>\n    <child link="left_thigh"/>\n    <origin xyz="0 0 0" rpy="0 0 0"/>\n    <axis xyz="1 0 0"/>  \x3c!-- X-axis rotation for hip pitch --\x3e\n    <limit lower="-2.0" upper="0.5" effort="100" velocity="1.0"/>\n  </joint>\n\n  <link name="left_shin">\n    <inertial>\n      <mass value="2.5"/>\n      <origin xyz="0 0 -0.2" rpy="0 0 0"/>\n      <inertia ixx="0.08" ixy="0.0" ixz="0.0" iyy="0.08" iyz="0.0" izz="0.08"/>\n    </inertial>\n    <visual>\n      <origin xyz="0 0 -0.2" rpy="0 0 0"/>\n      <geometry>\n        <cylinder radius="0.055" length="0.4"/>\n      </geometry>\n      <material name="green">\n        <color rgba="0.2 0.8 0.2 1"/>\n      </material>\n    </visual>\n    <collision>\n      <origin xyz="0 0 -0.2" rpy="0 0 0"/>\n      <geometry>\n        <cylinder radius="0.055" length="0.4"/>\n      </geometry>\n    </collision>\n  </link>\n\n  <joint name="left_knee" type="revolute">\n    <parent link="left_thigh"/>\n    <child link="left_shin"/>\n    <origin xyz="0 0 -0.4" rpy="0 0 0"/>\n    <axis xyz="1 0 0"/>  \x3c!-- X-axis rotation for knee --\x3e\n    <limit lower="0.0" upper="2.5" effort="100" velocity="1.0"/>\n  </joint>\n\n  <link name="left_foot">\n    <inertial>\n      <mass value="1.5"/>\n      <origin xyz="0.05 0 -0.05" rpy="0 0 0"/>\n      <inertia ixx="0.03" ixy="0.0" ixz="0.0" iyy="0.03" iyz="0.0" izz="0.03"/>\n    </inertial>\n    <visual>\n      <origin xyz="0.05 0 -0.05" rpy="0 0 0"/>\n      <geometry>\n        <box size="0.2 0.1 0.1"/>\n      </geometry>\n      <material name="green">\n        <color rgba="0.2 0.8 0.2 1"/>\n      </material>\n    </visual>\n    <collision>\n      <origin xyz="0.05 0 -0.05" rpy="0 0 0"/>\n      <geometry>\n        <box size="0.2 0.1 0.1"/>\n      </geometry>\n    </collision>\n  </link>\n\n  <joint name="left_ankle" type="revolute">\n    <parent link="left_shin"/>\n    <child link="left_foot"/>\n    <origin xyz="0 0 -0.4" rpy="0 0 0"/>\n    <axis xyz="1 0 0"/>  \x3c!-- X-axis rotation for ankle pitch --\x3e\n    <limit lower="-0.5" upper="0.5" effort="50" velocity="1.0"/>\n  </joint>\n\n  \x3c!-- Right Leg Chain (mirror of left) --\x3e\n  \x3c!-- WHAT: This defines the complete kinematic chain for the right leg --\x3e\n  \x3c!-- WHY: Symmetry is important for stable bipedal locomotion --\x3e\n  <link name="right_hip">\n    <inertial>\n      <mass value="2.0"/>\n      <origin xyz="0 0.05 0" rpy="0 0 0"/>\n      <inertia ixx="0.02" ixy="0.0" ixz="0.0" iyy="0.02" iyz="0.0" izz="0.02"/>\n    </inertial>\n    <visual>\n      <origin xyz="0 0.05 0" rpy="0 0 0"/>\n      <geometry>\n        <cylinder radius="0.06" length="0.1"/>\n      </geometry>\n      <material name="green">\n        <color rgba="0.2 0.8 0.2 1"/>\n      </material>\n    </visual>\n    <collision>\n      <origin xyz="0 0.05 0" rpy="0 0 0"/>\n      <geometry>\n        <cylinder radius="0.06" length="0.1"/>\n      </geometry>\n    </collision>\n  </link>\n\n  <joint name="right_hip_yaw" type="revolute">\n    <parent link="base_link"/>\n    <child link="right_hip"/>\n    <origin xyz="0 -0.08 0" rpy="0 0 0"/>\n    <axis xyz="0 0 1"/>  \x3c!-- Z-axis rotation for hip yaw --\x3e\n    <limit lower="-0.5" upper="0.5" effort="100" velocity="1.0"/>\n  </joint>\n\n  <link name="right_thigh">\n    <inertial>\n      <mass value="3.0"/>\n      <origin xyz="0 0 -0.2" rpy="0 0 0"/>\n      <inertia ixx="0.1" ixy="0.0" ixz="0.0" iyy="0.1" iyz="0.0" izz="0.1"/>\n    </inertial>\n    <visual>\n      <origin xyz="0 0 -0.2" rpy="0 0 0"/>\n      <geometry>\n        <cylinder radius="0.06" length="0.4"/>\n      </geometry>\n      <material name="green">\n        <color rgba="0.2 0.8 0.2 1"/>\n      </material>\n    </visual>\n    <collision>\n      <origin xyz="0 0 -0.2" rpy="0 0 0"/>\n      <geometry>\n        <cylinder radius="0.06" length="0.4"/>\n      </geometry>\n    </collision>\n  </link>\n\n  <joint name="right_hip_pitch" type="revolute">\n    <parent link="right_hip"/>\n    <child link="right_thigh"/>\n    <origin xyz="0 0 0" rpy="0 0 0"/>\n    <axis xyz="1 0 0"/>  \x3c!-- X-axis rotation for hip pitch --\x3e\n    <limit lower="-2.0" upper="0.5" effort="100" velocity="1.0"/>\n  </joint>\n\n  <link name="right_shin">\n    <inertial>\n      <mass value="2.5"/>\n      <origin xyz="0 0 -0.2" rpy="0 0 0"/>\n      <inertia ixx="0.08" ixy="0.0" ixz="0.0" iyy="0.08" iyz="0.0" izz="0.08"/>\n    </inertial>\n    <visual>\n      <origin xyz="0 0 -0.2" rpy="0 0 0"/>\n      <geometry>\n        <cylinder radius="0.055" length="0.4"/>\n      </geometry>\n      <material name="green">\n        <color rgba="0.2 0.8 0.2 1"/>\n      </material>\n    </visual>\n    <collision>\n      <origin xyz="0 0 -0.2" rpy="0 0 0"/>\n      <geometry>\n        <cylinder radius="0.055" length="0.4"/>\n      </geometry>\n    </collision>\n  </link>\n\n  <joint name="right_knee" type="revolute">\n    <parent link="right_thigh"/>\n    <child link="right_shin"/>\n    <origin xyz="0 0 -0.4" rpy="0 0 0"/>\n    <axis xyz="1 0 0"/>  \x3c!-- X-axis rotation for knee --\x3e\n    <limit lower="0.0" upper="2.5" effort="100" velocity="1.0"/>\n  </joint>\n\n  <link name="right_foot">\n    <inertial>\n      <mass value="1.5"/>\n      <origin xyz="0.05 0 -0.05" rpy="0 0 0"/>\n      <inertia ixx="0.03" ixy="0.0" ixz="0.0" iyy="0.03" iyz="0.0" izz="0.03"/>\n    </inertial>\n    <visual>\n      <origin xyz="0.05 0 -0.05" rpy="0 0 0"/>\n      <geometry>\n        <box size="0.2 0.1 0.1"/>\n      </geometry>\n      <material name="green">\n        <color rgba="0.2 0.8 0.2 1"/>\n      </material>\n    </visual>\n    <collision>\n      <origin xyz="0.05 0 -0.05" rpy="0 0 0"/>\n      <geometry>\n        <box size="0.2 0.1 0.1"/>\n      </geometry>\n    </collision>\n  </link>\n\n  <joint name="right_ankle" type="revolute">\n    <parent link="right_shin"/>\n    <child link="right_foot"/>\n    <origin xyz="0 0 -0.4" rpy="0 0 0"/>\n    <axis xyz="1 0 0"/>  \x3c!-- X-axis rotation for ankle pitch --\x3e\n    <limit lower="-0.5" upper="0.5" effort="50" velocity="1.0"/>\n  </joint>\n\n</robot>\n'})}),"\n",(0,o.jsx)(i.h2,{id:"xacro-example",children:"Xacro Example"}),"\n",(0,o.jsx)(i.p,{children:"Here's an example using Xacro for parameterized URDF:"}),"\n",(0,o.jsx)(i.pre,{children:(0,o.jsx)(i.code,{className:"language-xml",children:'<?xml version="1.0"?>\n\x3c!-- Example: Humanoid Robot with Xacro --\x3e\n\x3c!-- WHAT: This URDF uses Xacro to parameterize robot dimensions and properties --\x3e\n\x3c!-- WHY: To enable easy customization of robot models with different dimensions --\x3e\n\n<robot xmlns:xacro="http://www.ros.org/wiki/xacro" name="param_humanoid">\n\n  \x3c!-- Define robot parameters --\x3e\n  \x3c!-- WHAT: These properties define configurable parameters for the robot --\x3e\n  \x3c!-- WHY: Parameters allow for easy customization of robot dimensions and properties --\x3e\n  <xacro:property name="M_PI" value="3.1415926535897931" />\n  <xacro:property name="base_mass" value="15.0" />\n  <xacro:property name="torso_mass" value="10.0" />\n  <xacro:property name="arm_mass" value="1.5" />\n  <xacro:property name="leg_mass" value="3.0" />\n\n  \x3c!-- Define dimensions --\x3e\n  \x3c!-- WHAT: These properties define the physical dimensions of robot components --\x3e\n  \x3c!-- WHY: Dimensions can be easily modified to create robots of different sizes --\x3e\n  <xacro:property name="base_size_x" value="0.2" />\n  <xacro:property name="base_size_y" value="0.25" />\n  <xacro:property name="base_size_z" value="0.2" />\n\n  <xacro:property name="torso_height" value="0.6" />\n  <xacro:property name="torso_width" value="0.2" />\n  <xacro:property name="torso_depth" value="0.2" />\n\n  \x3c!-- Macro for creating a simple box link --\x3e\n  \x3c!-- WHAT: This macro defines a template for creating box-shaped links --\x3e\n  \x3c!-- WHY: Macros reduce code duplication and make the URDF more maintainable --\x3e\n  <xacro:macro name="box_link" params="name mass x y z *inertial *visual *collision">\n    <link name="${name}">\n      <inertial>\n        <mass value="${mass}"/>\n        <origin xyz="0 0 0" rpy="0 0 0"/>\n        <xacro:insert_block name="inertial"/>\n      </inertial>\n      <visual>\n        <origin xyz="0 0 0" rpy="0 0 0"/>\n        <xacro:insert_block name="visual"/>\n      </visual>\n      <collision>\n        <origin xyz="0 0 0" rpy="0 0 0"/>\n        <xacro:insert_block name="collision"/>\n      </collision>\n    </link>\n  </xacro:macro>\n\n  \x3c!-- Base link using the macro --\x3e\n  \x3c!-- WHAT: This creates the base link using the box_link macro --\x3e\n  \x3c!-- WHY: Using macros makes the URDF more concise and easier to maintain --\x3e\n  <xacro:box_link name="base_link" mass="${base_mass}">\n    <inertial>\n      <inertia ixx="0.5" ixy="0.0" ixz="0.0" iyy="0.5" iyz="0.0" izz="0.5"/>\n    </inertial>\n    <visual>\n      <geometry>\n        <box size="${base_size_x} ${base_size_y} ${base_size_z}"/>\n      </geometry>\n      <material name="gray">\n        <color rgba="0.5 0.5 0.5 1"/>\n      </material>\n    </visual>\n    <collision>\n      <geometry>\n        <box size="${base_size_x} ${base_size_y} ${base_size_z}"/>\n      </geometry>\n    </collision>\n  </xacro:box_link>\n\n  \x3c!-- Torso using the macro --\x3e\n  <xacro:box_link name="torso" mass="${torso_mass}">\n    <inertial>\n      <origin xyz="0 0 ${torso_height/2}" rpy="0 0 0"/>\n      <inertia ixx="0.8" ixy="0.0" ixz="0.0" iyy="0.8" iyz="0.0" izz="0.8"/>\n    </inertial>\n    <visual>\n      <origin xyz="0 0 ${torso_height/2}" rpy="0 0 0"/>\n      <geometry>\n        <box size="${torso_depth} ${torso_width} ${torso_height}"/>\n      </geometry>\n      <material name="red">\n        <color rgba="0.8 0.2 0.2 1"/>\n      </material>\n    </visual>\n    <collision>\n      <origin xyz="0 0 ${torso_height/2}" rpy="0 0 0"/>\n      <geometry>\n        <box size="${torso_depth} ${torso_width} ${torso_height}"/>\n      </geometry>\n    </collision>\n  </xacro:box_link>\n\n  \x3c!-- Joint connecting base to torso --\x3e\n  <joint name="torso_joint" type="fixed">\n    <parent link="base_link"/>\n    <child link="torso"/>\n    <origin xyz="0 0 ${base_size_z/2}" rpy="0 0 0"/>\n  </joint>\n\n  \x3c!-- Macro for creating arm chain --\x3e\n  \x3c!-- WHAT: This macro defines a template for creating complete arm kinematic chains --\x3e\n  \x3c!-- WHY: Macros allow for consistent arm structure while enabling customization --\x3e\n  <xacro:macro name="arm_chain" params="side position_x position_y shoulder_yaw_limit shoulder_pitch_limit elbow_limit">\n    \x3c!-- Shoulder --\x3e\n    <link name="${side}_shoulder">\n      <inertial>\n        <mass value="1.0"/>\n        <origin xyz="0 0 0" rpy="0 0 0"/>\n        <inertia ixx="0.01" ixy="0.0" ixz="0.0" iyy="0.01" iyz="0.0" izz="0.01"/>\n      </inertial>\n      <visual>\n        <origin xyz="0 0 0" rpy="0 0 0"/>\n        <geometry>\n          <cylinder radius="0.05" length="0.1"/>\n        </geometry>\n        <material name="blue">\n          <color rgba="0.2 0.2 0.8 1"/>\n        </material>\n      </visual>\n      <collision>\n        <origin xyz="0 0 0" rpy="0 0 0"/>\n        <geometry>\n          <cylinder radius="0.05" length="0.1"/>\n        </geometry>\n      </collision>\n    </link>\n\n    <joint name="${side}_shoulder_yaw" type="revolute">\n      <parent link="torso"/>\n      <child link="${side}_shoulder"/>\n      <origin xyz="${position_x} ${position_y} 0.3" rpy="0 0 0"/>\n      <axis xyz="0 0 1"/>\n      <limit lower="${-shoulder_yaw_limit}" upper="${shoulder_yaw_limit}" effort="50" velocity="2.0"/>\n    </joint>\n\n    \x3c!-- Upper arm --\x3e\n    <link name="${side}_upper_arm">\n      <inertial>\n        <mass value="${arm_mass}"/>\n        <origin xyz="0 0 -0.15" rpy="0 0 0"/>\n        <inertia ixx="0.03" ixy="0.0" ixz="0.0" iyy="0.03" iyz="0.0" izz="0.03"/>\n      </inertial>\n      <visual>\n        <origin xyz="0 0 -0.15" rpy="0 0 0"/>\n        <geometry>\n          <cylinder radius="0.04" length="0.3"/>\n        </geometry>\n        <material name="blue">\n          <color rgba="0.2 0.2 0.8 1"/>\n        </material>\n      </visual>\n      <collision>\n        <origin xyz="0 0 -0.15" rpy="0 0 0"/>\n        <geometry>\n          <cylinder radius="0.04" length="0.3"/>\n        </geometry>\n      </collision>\n    </link>\n\n    <joint name="${side}_shoulder_pitch" type="revolute">\n      <parent link="${side}_shoulder"/>\n      <child link="${side}_upper_arm"/>\n      <origin xyz="0 0 0" rpy="0 0 0"/>\n      <axis xyz="1 0 0"/>\n      <limit lower="${-shoulder_pitch_limit}" upper="${shoulder_pitch_limit}" effort="50" velocity="2.0"/>\n    </joint>\n\n    \x3c!-- Lower arm --\x3e\n    <link name="${side}_lower_arm">\n      <inertial>\n        <mass value="1.0"/>\n        <origin xyz="0 0 -0.12" rpy="0 0 0"/>\n        <inertia ixx="0.02" ixy="0.0" ixz="0.0" iyy="0.02" iyz="0.0" izz="0.02"/>\n      </inertial>\n      <visual>\n        <origin xyz="0 0 -0.12" rpy="0 0 0"/>\n        <geometry>\n          <cylinder radius="0.035" length="0.24"/>\n        </geometry>\n        <material name="blue">\n          <color rgba="0.2 0.2 0.8 1"/>\n        </material>\n      </visual>\n      <collision>\n        <origin xyz="0 0 -0.12" rpy="0 0 0"/>\n        <geometry>\n          <cylinder radius="0.035" length="0.24"/>\n        </geometry>\n      </collision>\n    </link>\n\n    <joint name="${side}_elbow" type="revolute">\n      <parent link="${side}_upper_arm"/>\n      <child link="${side}_lower_arm"/>\n      <origin xyz="0 0 -0.3" rpy="0 0 0"/>\n      <axis xyz="1 0 0"/>\n      <limit lower="${-elbow_limit}" upper="0.0" effort="30" velocity="2.0"/>\n    </joint>\n  </xacro:macro>\n\n  \x3c!-- Create both arms using the macro --\x3e\n  \x3c!-- WHAT: This instantiates both left and right arms using the arm_chain macro --\x3e\n  \x3c!-- WHY: Using macros ensures consistency between left and right arms --\x3e\n  <xacro:arm_chain side="left" position_x="0.05" position_y="0.1" shoulder_yaw_limit="1.57" shoulder_pitch_limit="2.0" elbow_limit="2.5"/>\n  <xacro:arm_chain side="right" position_x="0.05" position_y="-0.1" shoulder_yaw_limit="1.57" shoulder_pitch_limit="2.0" elbow_limit="2.5"/>\n\n  \x3c!-- Gazebo plugin --\x3e\n  <gazebo>\n    <plugin name="gazebo_ros_control" filename="libgazebo_ros_control.so">\n      <robotNamespace>/humanoid</robotNamespace>\n    </plugin>\n  </gazebo>\n\n</robot>\n'})}),"\n",(0,o.jsx)(i.h2,{id:"dependencies",children:"Dependencies"}),"\n",(0,o.jsxs)(i.ul,{children:["\n",(0,o.jsx)(i.li,{children:"XML parser (built into ROS 2)"}),"\n",(0,o.jsx)(i.li,{children:"URDF parser and tools for validation"}),"\n"]}),"\n",(0,o.jsx)(i.h2,{id:"how-to-use",children:"How to Use"}),"\n",(0,o.jsx)(i.pre,{children:(0,o.jsx)(i.code,{className:"language-bash",children:"# Validate the URDF file\ncheck_urdf /path/to/your/robot.urdf\n\n# Convert Xacro to URDF\nros2 run xacro xacro input_file.xacro > output_file.urdf\n\n# Visualize in RViz\nros2 run rviz2 rviz2\n# Then add a RobotModel display and set the robot description parameter\n"})}),"\n",(0,o.jsx)(i.h2,{id:"key-components-explained",children:"Key Components Explained"}),"\n",(0,o.jsxs)(i.ol,{children:["\n",(0,o.jsxs)(i.li,{children:[(0,o.jsx)(i.strong,{children:"Links"}),": Represent rigid bodies with mass, inertia, and geometry"]}),"\n",(0,o.jsxs)(i.li,{children:[(0,o.jsx)(i.strong,{children:"Joints"}),": Define connections between links with specific movement types"]}),"\n",(0,o.jsxs)(i.li,{children:[(0,o.jsx)(i.strong,{children:"Inertial"}),": Physical properties for simulation (mass, center of mass, inertia)"]}),"\n",(0,o.jsxs)(i.li,{children:[(0,o.jsx)(i.strong,{children:"Visual"}),": How the link appears in visualization tools"]}),"\n",(0,o.jsxs)(i.li,{children:[(0,o.jsx)(i.strong,{children:"Collision"}),": How the link interacts in physics simulation"]}),"\n",(0,o.jsxs)(i.li,{children:[(0,o.jsx)(i.strong,{children:"Materials"}),": Color and appearance properties"]}),"\n",(0,o.jsxs)(i.li,{children:[(0,o.jsx)(i.strong,{children:"Gazebo plugins"}),": Configuration for physics simulation"]}),"\n"]})]})}function m(n={}){const{wrapper:i}={...(0,t.R)(),...n.components};return i?(0,o.jsx)(i,{...n,children:(0,o.jsx)(y,{...n})}):y(n)}}}]);